$(document).ready(function(){
    $("#open-sidebar").click(function(){
        if($("#sidebar").hasClass("open-sidebar")){
        $(".main-menu").removeClass("open-sidebar");
        $(".main").removeClass("shift-right");
        if($(window).width()>768)
            {    
                
                $(".main").css("margin-left","50");
                
            }
            else{
                $(".main").css("margin-left","0");
            }
        }
        else{
           $(".main-menu").addClass("open-sidebar");
           $(".main").addClass("shift-right");
           $(".main").css("margin-left","250");
       
        }
    });

   });

